import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Navigation, CheckCircle } from 'lucide-react';
import { useAuth } from '../context/AuthContext';

const BRANCHES = [
  {
    id: 1,
    name: 'GoldenCream Dairy - Hyderabad',
    city: 'Hyderabad',
    address: 'Banjara Hills, Road No. 12, Hyderabad, Telangana 500034',
    image: 'https://images.unsplash.com/photo-1549921296-3a92f7d8b47d',
    coordinates: { lat: 17.3850, lng: 78.4867 },
    phone: '+91 40 1234 5678',
    hours: '6:00 AM - 10:00 PM'
  },
  {
    id: 2,
    name: 'GoldenCream Dairy - Mumbai',
    city: 'Mumbai',
    address: 'Bandra West, Linking Road, Mumbai, Maharashtra 400050',
    image: 'https://images.unsplash.com/photo-1512453979798-5ea266f8880c',
    coordinates: { lat: 19.0760, lng: 72.8777 },
    phone: '+91 22 1234 5678',
    hours: '5:30 AM - 11:00 PM'
  },
  {
    id: 3,
    name: 'GoldenCream Dairy - Bangalore',
    city: 'Bangalore',
    address: 'Indiranagar, 100 Feet Road, Bangalore, Karnataka 560038',
    image: 'https://images.unsplash.com/photo-1505761671935-60b3a7427bad',
    coordinates: { lat: 12.9716, lng: 77.5946 },
    phone: '+91 80 1234 5678',
    hours: '6:00 AM - 10:30 PM'
  },
  {
    id: 4,
    name: 'GoldenCream Dairy - Delhi',
    city: 'Delhi',
    address: 'Connaught Place, New Delhi, Delhi 110001',
    image: 'https://images.unsplash.com/photo-1582719478176-2f30acdf2df9',
    coordinates: { lat: 28.6139, lng: 77.2090 },
    phone: '+91 11 1234 5678',
    hours: '5:00 AM - 11:00 PM'
  },
  {
    id: 5,
    name: 'GoldenCream Dairy - Chennai',
    city: 'Chennai',
    address: 'T Nagar, Usman Road, Chennai, Tamil Nadu 600017',
    image: 'https://images.unsplash.com/photo-1550583724-b2692b85b150',
    coordinates: { lat: 13.0827, lng: 80.2707 },
    phone: '+91 44 1234 5678',
    hours: '5:30 AM - 10:30 PM'
  },
  {
    id: 6,
    name: 'GoldenCream Dairy - Pune',
    city: 'Pune',
    address: 'Koregaon Park, North Main Road, Pune, Maharashtra 411001',
    image: 'https://images.unsplash.com/photo-1582719478176-2f30acdf2df9',
    coordinates: { lat: 18.5204, lng: 73.8567 },
    phone: '+91 20 1234 5678',
    hours: '6:00 AM - 10:00 PM'
  }
];

export default function BranchSelectionPage() {
  const navigate = useNavigate();
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [userLocation, setUserLocation] = useState(null);
  const [detectingLocation, setDetectingLocation] = useState(false);
  const [nearestBranch, setNearestBranch] = useState(null);

  // Calculate distance between two coordinates
  const calculateDistance = (lat1, lon1, lat2, lon2) => {
    const R = 6371; // Earth's radius in kilometers
    const dLat = (lat2 - lat1) * Math.PI / 180;
    const dLon = (lon2 - lon1) * Math.PI / 180;
    const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
              Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
              Math.sin(dLon/2) * Math.sin(dLon/2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
    return R * c;
  };

  // Detect user location
  const detectLocation = () => {
    setDetectingLocation(true);
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ lat: latitude, lng: longitude });
          
          // Find nearest branch
          let nearest = null;
          let minDistance = Infinity;
          
          BRANCHES.forEach(branch => {
            const distance = calculateDistance(
              latitude, longitude,
              branch.coordinates.lat, branch.coordinates.lng
            );
            if (distance < minDistance) {
              minDistance = distance;
              nearest = { ...branch, distance: distance.toFixed(1) };
            }
          });
          
          setNearestBranch(nearest);
          setDetectingLocation(false);
        },
        (error) => {
          console.error('Error getting location:', error);
          setDetectingLocation(false);
        }
      );
    } else {
      setDetectingLocation(false);
    }
  };

  const handleBranchSelect = (branch) => {
    setSelectedBranch(branch);
    // Store branch info in localStorage
    localStorage.setItem('selectedBranch', JSON.stringify(branch));
    localStorage.setItem('userLocation', JSON.stringify(userLocation));
    
    // Navigate to home page
    setTimeout(() => {
      navigate('/');
    }, 500);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-dairy-cream to-white">
      <div className="container mx-auto px-4 py-8">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-8"
        >
          <h1 className="text-4xl md:text-5xl font-bold text-gray-800 mb-4">
            Select Your Nearest Branch
          </h1>
          <p className="text-xl text-gray-600 max-w-2xl mx-auto">
            Choose the GoldenCream Dairy branch closest to you for the freshest dairy products
          </p>
        </motion.div>

        {/* Location Detection */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, delay: 0.2 }}
          className="text-center mb-8"
        >
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={detectLocation}
            disabled={detectingLocation}
            className="bg-dairy-green text-white px-6 py-3 rounded-lg font-semibold flex items-center gap-2 mx-auto hover:bg-green-600 transition-colors disabled:opacity-50"
          >
            {detectingLocation ? (
              <span className="animate-spin rounded-full h-5 w-5 border-t-2 border-white border-solid"></span>
            ) : (
              <Navigation size={20} />
            )}
            {detectingLocation ? 'Detecting Location...' : 'Detect My Location'}
          </motion.button>
          
          {nearestBranch && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="mt-4 p-4 bg-green-50 border border-green-200 rounded-lg inline-block"
            >
              <p className="text-green-800 font-medium">
                üìç Nearest branch: {nearestBranch.name} ({nearestBranch.distance} km away)
              </p>
            </motion.div>
          )}
        </motion.div>

        {/* Branch Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 max-w-7xl mx-auto">
          {BRANCHES.map((branch, index) => (
            <motion.div
              key={branch.id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ y: -5, scale: 1.02 }}
              className="bg-white rounded-xl shadow-lg overflow-hidden border border-gray-100 hover:shadow-xl transition-all duration-300"
            >
              <div className="relative h-48 overflow-hidden">
                <img
                  src={branch.image}
                  alt={branch.name}
                  className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent"></div>
                <div className="absolute bottom-4 left-4 text-white">
                  <h3 className="font-bold text-lg">{branch.city}</h3>
                  <p className="text-sm opacity-90">{branch.hours}</p>
                </div>
                {nearestBranch?.id === branch.id && (
                  <div className="absolute top-4 right-4 bg-green-500 text-white px-2 py-1 rounded-full text-xs font-medium flex items-center gap-1">
                    <CheckCircle size={12} />
                    Nearest
                  </div>
                )}
              </div>
              
              <div className="p-6">
                <h4 className="font-bold text-lg text-gray-800 mb-2">{branch.name}</h4>
                <div className="flex items-start gap-2 text-gray-600 mb-3">
                  <MapPin size={16} className="mt-1 flex-shrink-0" />
                  <p className="text-sm">{branch.address}</p>
                </div>
                <p className="text-sm text-gray-500 mb-4">{branch.phone}</p>
                
                <motion.button
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  onClick={() => handleBranchSelect(branch)}
                  className="w-full bg-dairy-green text-white py-3 rounded-lg font-semibold hover:bg-green-600 transition-colors"
                >
                  Select Branch
                </motion.button>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Selected Branch Animation */}
        <AnimatePresence>
          {selectedBranch && (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              exit={{ opacity: 0, scale: 0.8 }}
              className="fixed inset-0 bg-black/50 flex items-center justify-center z-50"
            >
              <motion.div
                initial={{ y: 50 }}
                animate={{ y: 0 }}
                className="bg-white rounded-2xl p-8 max-w-md mx-4 text-center"
              >
                <div className="text-6xl mb-4">üéâ</div>
                <h3 className="text-2xl font-bold text-gray-800 mb-2">
                  Branch Selected!
                </h3>
                <p className="text-gray-600 mb-4">
                  You've selected {selectedBranch.name}
                </p>
                <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-dairy-green border-solid mx-auto"></div>
                <p className="text-sm text-gray-500 mt-2">Redirecting to homepage...</p>
              </motion.div>
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}
