import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { MapPin, Navigation, Clock, Truck, X, Search, CheckCircle } from 'lucide-react';
import LocationMap from './LocationMap';
import { useLocation } from '../hooks/useLocation';

// Dummy data for nearby services
const NEARBY_SERVICES = {
  'New Delhi': [
    { name: 'GoldenCream Farm - Delhi', distance: '2.5 km', delivery: true, eta: '30 mins', rating: 4.8 },
    { name: 'Fresh Dairy Co.', distance: '4.1 km', delivery: true, eta: '45 mins', rating: 4.5 },
    { name: 'Organic Milk Hub', distance: '6.2 km', delivery: false, eta: 'N/A', rating: 4.2 }
  ],
  'Mumbai': [
    { name: 'GoldenCream Farm - Mumbai', distance: '1.8 km', delivery: true, eta: '25 mins', rating: 4.9 },
    { name: 'Mumbai Dairy Fresh', distance: '3.5 km', delivery: true, eta: '40 mins', rating: 4.6 },
    { name: 'Coastal Dairy', distance: '5.1 km', delivery: false, eta: 'N/A', rating: 4.3 }
  ],
  'Bangalore': [
    { name: 'GoldenCream Farm - Bangalore', distance: '3.2 km', delivery: true, eta: '35 mins', rating: 4.7 },
    { name: 'Garden City Dairy', distance: '4.8 km', delivery: true, eta: '50 mins', rating: 4.4 },
    { name: 'Tech Dairy Hub', distance: '7.1 km', delivery: false, eta: 'N/A', rating: 4.1 }
  ],
  'default': [
    { name: 'GoldenCream Farm', distance: '3.0 km', delivery: true, eta: '35 mins', rating: 4.6 },
    { name: 'Local Dairy Co.', distance: '5.2 km', delivery: true, eta: '45 mins', rating: 4.3 },
    { name: 'Fresh Farm Dairy', distance: '8.1 km', delivery: false, eta: 'N/A', rating: 4.0 }
  ]
};

const LocationDetector = () => {
  const { location, loading, error, detectLocation, setManualLocation: setLocationData, setError } = useLocation();
  const [showManualInput, setShowManualInput] = useState(false);
  const [manualLocationInput, setManualLocationInput] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showBanner, setShowBanner] = useState(true);

  const handleManualLocationChange = (value) => {
    setManualLocationInput(value);
    if (value.length > 2) {
      // Simulate location suggestions
      const cities = ['New Delhi', 'Mumbai', 'Bangalore', 'Chennai', 'Kolkata', 'Hyderabad'];
      const filtered = cities.filter(city => 
        city.toLowerCase().includes(value.toLowerCase())
      );
      setSuggestions(filtered);
    } else {
      setSuggestions([]);
    }
  };

  const selectManualLocation = (selectedLocation) => {
    const locationData = {
      city: selectedLocation,
      state: '',
      fullLocation: selectedLocation,
      coordinates: null,
      timestamp: new Date().toISOString()
    };
    setLocationData(locationData);
    setManualLocationInput('');
    setSuggestions([]);
    setShowManualInput(false);
  };

  const getNearbyServices = () => {
    if (!location) return [];
    return NEARBY_SERVICES[location.city] || NEARBY_SERVICES['default'];
  };

  return (
    <>
      {/* Location Banner */}
      <AnimatePresence>
        {showBanner && (
          <motion.div
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -50 }}
            className="fixed top-0 left-0 right-0 z-50 bg-dairy-green text-white p-4 shadow-lg"
          >
            <div className="container-custom flex items-center justify-between">
              <div className="flex items-center gap-3">
                <MapPin size={20} />
                {location ? (
                  <span className="font-medium">
                    You are browsing from <strong>{location.fullLocation}</strong>
                  </span>
                ) : (
                  <span>Detecting your location...</span>
                )}
              </div>
              <div className="flex items-center gap-2">
                {!location && (
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={detectLocation}
                    disabled={loading}
                    className="bg-white/20 hover:bg-white/30 px-3 py-1 rounded-lg text-sm font-medium transition-colors"
                  >
                    {loading ? 'Detecting...' : 'Detect Location'}
                  </motion.button>
                )}
                <button
                  onClick={() => setShowManualInput(true)}
                  className="bg-white/20 hover:bg-white/30 px-3 py-1 rounded-lg text-sm font-medium transition-colors"
                >
                  Change
                </button>
                <button
                  onClick={() => setShowBanner(false)}
                  className="text-white/70 hover:text-white"
                >
                  <X size={16} />
                </button>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Manual Location Input Modal */}
      <AnimatePresence>
        {showManualInput && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/50 z-50 flex items-center justify-center p-4"
            onClick={() => setShowManualInput(false)}
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
              className="bg-white rounded-2xl p-6 w-full max-w-md shadow-2xl"
            >
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-xl font-bold text-gray-800">Enter Your Location</h3>
                <button
                  onClick={() => setShowManualInput(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X size={20} />
                </button>
              </div>
              
              <div className="relative">
                <div className="flex items-center gap-2 mb-4">
                  <Search size={20} className="text-dairy-green" />
                  <input
                    type="text"
                    placeholder="Enter your city..."
                    value={manualLocationInput}
                    onChange={(e) => handleManualLocationChange(e.target.value)}
                    className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-dairy-green"
                  />
                </div>
                
                {suggestions.length > 0 && (
                  <div className="absolute top-full left-0 right-0 bg-white border border-gray-200 rounded-lg shadow-lg z-10">
                    {suggestions.map((suggestion, index) => (
                      <button
                        key={index}
                        onClick={() => selectManualLocation(suggestion)}
                        className="w-full text-left px-4 py-2 hover:bg-dairy-cream/50 flex items-center gap-2"
                      >
                        <MapPin size={16} className="text-dairy-green" />
                        {suggestion}
                      </button>
                    ))}
                  </div>
                )}
              </div>

              <div className="flex gap-3 mt-4">
                <button
                  onClick={detectLocation}
                  disabled={loading}
                  className="flex-1 bg-dairy-green text-white py-2 rounded-lg font-medium hover:bg-green-600 transition-colors disabled:opacity-50"
                >
                  {loading ? 'Detecting...' : 'Auto Detect'}
                </button>
                <button
                  onClick={() => setShowManualInput(false)}
                  className="flex-1 bg-gray-200 text-gray-700 py-2 rounded-lg font-medium hover:bg-gray-300 transition-colors"
                >
                  Cancel
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Error Message */}
      <AnimatePresence>
        {error && (
          <motion.div
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            className="fixed top-20 left-1/2 transform -translate-x-1/2 z-40 bg-red-100 border border-red-300 text-red-700 px-4 py-2 rounded-lg shadow-lg"
          >
            {error}
          </motion.div>
        )}
      </AnimatePresence>

      {/* Nearby Services Section */}
      {location && (
        <motion.section
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          className="section-padding bg-dairy-cream/30"
        >
          <div className="container-custom">
            <div className="text-center mb-12">
              <h2 className="text-3xl md:text-4xl font-bold text-gray-800 mb-4">
                Services Near You
              </h2>
              <p className="text-lg text-gray-600 max-w-2xl mx-auto">
                Discover dairy farms and delivery services available in your area
              </p>
            </div>

            {/* Location Map */}
            <div className="mb-12">
              <LocationMap location={location} />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {getNearbyServices().map((service, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className="bg-white rounded-xl p-6 shadow-lg hover:shadow-xl transition-shadow"
                >
                  <div className="flex items-start justify-between mb-4">
                    <div>
                      <h3 className="font-bold text-lg text-gray-800 mb-1">
                        {service.name}
                      </h3>
                      <p className="text-gray-600 text-sm">
                        {service.distance} away
                      </p>
                    </div>
                    <div className="flex items-center gap-1">
                      <span className="text-yellow-500">★</span>
                      <span className="text-sm font-medium">{service.rating}</span>
                    </div>
                  </div>

                  <div className="space-y-3">
                    <div className="flex items-center gap-2">
                      <Truck size={16} className="text-dairy-green" />
                      <span className="text-sm">
                        Delivery: {service.delivery ? (
                          <span className="text-green-600 font-medium flex items-center gap-1">
                            <CheckCircle size={14} />
                            Available
                          </span>
                        ) : (
                          <span className="text-red-600 font-medium">Not Available</span>
                        )}
                      </span>
                    </div>

                    {service.delivery && (
                      <div className="flex items-center gap-2">
                        <Clock size={16} className="text-dairy-green" />
                        <span className="text-sm">
                          ETA: <span className="text-dairy-green font-medium">{service.eta}</span>
                        </span>
                      </div>
                    )}
                  </div>

                  <motion.button
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                    className="w-full mt-4 bg-dairy-green text-white py-2 rounded-lg font-medium hover:bg-green-600 transition-colors"
                  >
                    {service.delivery ? 'Order Now' : 'View Details'}
                  </motion.button>
                </motion.div>
              ))}
            </div>
          </div>
        </motion.section>
      )}
    </>
  );
};

export default LocationDetector;
