import React from 'react';
import { motion } from 'framer-motion';
import { MapPin } from 'lucide-react';

const LocationMap = ({ location, className = '' }) => {
  if (!location || !location.coordinates) {
    return null;
  }

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.95 }}
      animate={{ opacity: 1, scale: 1 }}
      className={`relative rounded-xl overflow-hidden shadow-lg ${className}`}
    >
      {/* Map placeholder - in a real app, this would be Google Maps or similar */}
      <div className="w-full h-64 bg-gradient-to-br from-dairy-green/20 to-dairy-cream/40 relative">
        {/* Map grid pattern */}
        <div className="absolute inset-0 opacity-10">
          <div className="w-full h-full" style={{
            backgroundImage: `
              linear-gradient(rgba(127, 176, 105, 0.1) 1px, transparent 1px),
              linear-gradient(90deg, rgba(127, 176, 105, 0.1) 1px, transparent 1px)
            `,
            backgroundSize: '20px 20px'
          }} />
        </div>
        
        {/* Location pin */}
        <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.5, type: 'spring' }}
            className="relative"
          >
            <MapPin size={32} className="text-dairy-green drop-shadow-lg" />
            <motion.div
              animate={{ scale: [1, 1.2, 1] }}
              transition={{ duration: 2, repeat: Infinity }}
              className="absolute inset-0 bg-dairy-green/30 rounded-full"
              style={{ width: '32px', height: '32px' }}
            />
          </motion.div>
        </div>
        
        {/* Location label */}
        <div className="absolute bottom-4 left-4 bg-white/90 backdrop-blur-sm rounded-lg px-3 py-2 shadow-lg">
          <p className="text-sm font-medium text-gray-800">
            üìç {location.fullLocation}
          </p>
        </div>
      </div>
    </motion.div>
  );
};

export default LocationMap;
